import React, { Component } from 'react'
import logo from './logo.svg';
import './App.css'


class App extends Component {

	constructor(props){
		super(props);
		this.state = {
			num : 0,

		}
	}

	plusNum = () =>{
		this.setState({
			num : this.state.num + 1,
		})
	}

	minusNum = () =>{
		this.setState({
			num : this.state.num - 1,
		})
	}


    render() {
        return (
            <div>
                <h3>카운터</h3>
				<div>
					값 : {this.state.num}
				</div>
				<button onClick={this.plusNum}>+</button>
				<button onClick={this.minusNum}>-</button>
            </div>
        )
    }
}


export default App;
